<script>import { T, forwardEventHandlers } from '@threlte/core';
import Api from './Api.svelte';
import { InstancedMesh } from 'three';
export let id = 'default';
export let limit = 1000;
export let range = 1000;
export let update = true;
export const ref = new InstancedMesh(null, null, 0);
const dispatchingComponent = forwardEventHandlers();
const args = [null, null, 0];
</script>

<T
  is={ref}
  raycast={() => null}
  matrixAutoUpdate={false}
  {args}
  {...$$restProps}
  bind:bind={$dispatchingComponent}
>
  <Api
    instancedMesh={ref}
    {id}
    {limit}
    {range}
    {update}
  >
    <slot {ref} />
  </Api>
</T>
