<script>import { forwardEventHandlers, T, useTask, useParent, useThrelte } from '@threlte/core';
import { OrbitControls as ThreeOrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import { onDestroy } from 'svelte';
import { useControlsContext } from '../useControlsContext';
const parent = useParent();
const isCamera = (p) => {
    return p.isCamera;
};
const { renderer, invalidate } = useThrelte();
if (!isCamera($parent)) {
    throw new Error('Parent missing: <OrbitControls> need to be a child of a <Camera>');
}
export const ref = new ThreeOrbitControls($parent, renderer.domElement);
const { start, stop } = useTask(ref.update, {
    autoStart: false,
    autoInvalidate: false
});
$: {
    if ($$restProps.autoRotate || $$restProps.enableDamping)
        start();
    else
        stop();
}
const component = forwardEventHandlers();
const { orbitControls } = useControlsContext();
orbitControls.set(ref);
onDestroy(() => orbitControls.set(undefined));
</script>

<T
  is={ref}
  let:ref
  {...$$restProps}
  bind:this={$component}
  on:change={invalidate}
>
  <slot {ref} />
</T>
